PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "migrations" ("id" integer primary key autoincrement not null, "migration" varchar not null, "batch" integer not null);
CREATE TABLE IF NOT EXISTS "password_reset_tokens" ("email" varchar not null, "token" varchar not null, "created_at" datetime, primary key ("email"));
CREATE TABLE IF NOT EXISTS "sessions" ("id" varchar not null, "user_id" integer, "ip_address" varchar, "user_agent" text, "payload" text not null, "last_activity" integer not null, primary key ("id"));
CREATE TABLE IF NOT EXISTS "cache" ("key" varchar not null, "value" text not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "cache_locks" ("key" varchar not null, "owner" varchar not null, "expiration" integer not null, primary key ("key"));
CREATE TABLE IF NOT EXISTS "jobs" ("id" integer primary key autoincrement not null, "queue" varchar not null, "payload" text not null, "attempts" integer not null, "reserved_at" integer, "available_at" integer not null, "created_at" integer not null);
CREATE TABLE IF NOT EXISTS "job_batches" ("id" varchar not null, "name" varchar not null, "total_jobs" integer not null, "pending_jobs" integer not null, "failed_jobs" integer not null, "failed_job_ids" text not null, "options" text, "cancelled_at" integer, "created_at" integer not null, "finished_at" integer, primary key ("id"));
CREATE TABLE IF NOT EXISTS "failed_jobs" ("id" integer primary key autoincrement not null, "uuid" varchar not null, "connection" text not null, "queue" text not null, "payload" text not null, "exception" text not null, "failed_at" datetime not null default CURRENT_TIMESTAMP);
CREATE TABLE IF NOT EXISTS "roles" ("id" integer primary key autoincrement not null, "name" varchar not null, "slug" varchar not null, "description" text, "permissions" text, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "upazilas" ("id" integer primary key autoincrement not null, "name" varchar not null, "name_bn" varchar, "slug" varchar not null, "description" text, "description_bn" text, "image" varchar, "map_embed" varchar, "latitude" numeric, "longitude" numeric, "population" integer, "area" numeric, "is_active" tinyint(1) not null default '1', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "categories" ("id" integer primary key autoincrement not null, "name" varchar not null, "name_bn" varchar, "slug" varchar not null, "description" text, "icon" varchar, "image" varchar, "color" varchar not null default '#007bff', "parent_id" integer, "is_active" tinyint(1) not null default '1', "show_in_menu" tinyint(1) not null default '1', "allow_user_submission" tinyint(1) not null default '1', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("parent_id") references "categories"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "users" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "email_verified_at" datetime, "password" varchar not null, "remember_token" varchar, "created_at" datetime, "updated_at" datetime, "role_id" integer, "upazila_id" integer, "phone" varchar, "avatar" varchar, "address" text, "bio" text, "status" varchar check ("status" in ('pending', 'active', 'suspended')) not null default 'pending', "is_verified" tinyint(1) not null default '0', "approved_at" datetime, "approved_by" integer, "nid_number" varchar, "registration_purpose" text, "requested_categories" text, foreign key("role_id") references "roles"("id") on delete set null, foreign key("upazila_id") references "upazilas"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "listings" ("id" integer primary key autoincrement not null, "user_id" integer not null, "category_id" integer not null, "upazila_id" integer not null, "title" varchar not null, "title_bn" varchar, "slug" varchar not null, "short_description" text, "description" text, "image" varchar, "gallery" text, "address" varchar, "phone" varchar, "email" varchar, "website" varchar, "facebook" varchar, "youtube" varchar, "latitude" numeric, "longitude" numeric, "map_embed" varchar, "opening_hours" text, "price_from" numeric, "price_to" numeric, "features" text, "extra_fields" text, "status" varchar check ("status" in ('pending', 'approved', 'rejected')) not null default 'pending', "is_featured" tinyint(1) not null default '0', "views" integer not null default '0', "approved_at" datetime, "approved_by" integer, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("category_id") references "categories"("id") on delete cascade, foreign key("upazila_id") references "upazilas"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "comments" ("id" integer primary key autoincrement not null, "commentable_type" varchar not null, "commentable_id" integer not null, "user_id" integer, "parent_id" integer, "name" varchar not null, "email" varchar not null, "phone" varchar, "content" text not null, "rating" integer, "status" varchar check ("status" in ('pending', 'approved', 'rejected')) not null default 'pending', "ip_address" varchar, "approved_at" datetime, "approved_by" integer, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete set null, foreign key("parent_id") references "comments"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "mp_profiles" ("id" integer primary key autoincrement not null, "name" varchar not null, "name_bn" varchar, "slug" varchar not null, "designation" varchar, "constituency" varchar, "bio" text, "image" varchar, "phone" varchar, "email" varchar, "facebook" varchar, "twitter" varchar, "address" text, "elected_date" date, "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "mp_questions" ("id" integer primary key autoincrement not null, "mp_profile_id" integer not null, "user_id" integer, "name" varchar not null, "email" varchar not null, "phone" varchar, "question" text not null, "answer" text, "status" varchar check ("status" in ('pending', 'approved', 'answered', 'rejected')) not null default 'pending', "is_public" tinyint(1) not null default '1', "answered_at" datetime, "answered_by" integer, "created_at" datetime, "updated_at" datetime, foreign key("mp_profile_id") references "mp_profiles"("id") on delete cascade, foreign key("user_id") references "users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "site_settings" ("id" integer primary key autoincrement not null, "key" varchar not null, "value" text, "type" varchar not null default 'text', "group" varchar not null default 'general', "label" varchar, "description" text, "is_public" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "sliders" ("id" integer primary key autoincrement not null, "title" varchar, "title_bn" varchar, "subtitle" text, "image" varchar not null, "link" varchar, "button_text" varchar, "is_active" tinyint(1) not null default '1', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "pages" ("id" integer primary key autoincrement not null, "title" varchar not null, "title_bn" varchar, "slug" varchar not null, "content" text, "featured_image" varchar, "meta_title" varchar, "meta_description" text, "meta_keywords" varchar, "is_active" tinyint(1) not null default '1', "template" varchar not null default 'default', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "menus" ("id" integer primary key autoincrement not null, "name" varchar not null, "location" varchar not null, "is_active" tinyint(1) not null default '1', "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "menu_items" ("id" integer primary key autoincrement not null, "menu_id" integer not null, "parent_id" integer, "title" varchar not null, "title_bn" varchar, "url" varchar, "route_name" varchar, "route_params" text, "icon" varchar, "target" varchar not null default '_self', "is_active" tinyint(1) not null default '1', "sort_order" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("menu_id") references "menus"("id") on delete cascade, foreign key("parent_id") references "menu_items"("id") on delete cascade);
CREATE TABLE IF NOT EXISTS "news" ("id" integer primary key autoincrement not null, "user_id" integer, "title" varchar not null, "title_bn" varchar, "slug" varchar not null, "excerpt" text, "content" text not null, "image" varchar, "gallery" text, "type" varchar check ("type" in ('news', 'notice', 'event')) not null default 'news', "event_date" date, "is_featured" tinyint(1) not null default '0', "is_active" tinyint(1) not null default '1', "views" integer not null default '0', "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete set null);
CREATE TABLE IF NOT EXISTS "contacts" ("id" integer primary key autoincrement not null, "name" varchar not null, "email" varchar not null, "phone" varchar, "subject" varchar not null, "message" text not null, "status" varchar check ("status" in ('unread', 'read', 'replied')) not null default 'unread', "reply" text, "replied_at" datetime, "replied_by" integer, "created_at" datetime, "updated_at" datetime);
CREATE TABLE IF NOT EXISTS "user_category_permissions" ("id" integer primary key autoincrement not null, "user_id" integer not null, "category_id" integer not null, "is_approved" tinyint(1) not null default '0', "approved_by" integer, "approved_at" datetime, "created_at" datetime, "updated_at" datetime, foreign key("user_id") references "users"("id") on delete cascade, foreign key("category_id") references "categories"("id") on delete cascade, foreign key("approved_by") references "users"("id") on delete set null);
DELETE FROM sqlite_sequence;
CREATE INDEX "sessions_user_id_index" on "sessions" ("user_id");
CREATE INDEX "sessions_last_activity_index" on "sessions" ("last_activity");
CREATE INDEX "cache_expiration_index" on "cache" ("expiration");
CREATE INDEX "cache_locks_expiration_index" on "cache_locks" ("expiration");
CREATE INDEX "jobs_queue_index" on "jobs" ("queue");
CREATE UNIQUE INDEX "failed_jobs_uuid_unique" on "failed_jobs" ("uuid");
CREATE UNIQUE INDEX "roles_name_unique" on "roles" ("name");
CREATE UNIQUE INDEX "roles_slug_unique" on "roles" ("slug");
CREATE UNIQUE INDEX "upazilas_slug_unique" on "upazilas" ("slug");
CREATE UNIQUE INDEX "categories_slug_unique" on "categories" ("slug");
CREATE UNIQUE INDEX "users_email_unique" on "users" ("email");
CREATE INDEX "listings_status_category_id_upazila_id_index" on "listings" ("status", "category_id", "upazila_id");
CREATE INDEX "listings_is_featured_status_index" on "listings" ("is_featured", "status");
CREATE UNIQUE INDEX "listings_slug_unique" on "listings" ("slug");
CREATE INDEX "comments_commentable_type_commentable_id_index" on "comments" ("commentable_type", "commentable_id");
CREATE INDEX "comments_commentable_type_commentable_id_status_index" on "comments" ("commentable_type", "commentable_id", "status");
CREATE UNIQUE INDEX "mp_profiles_slug_unique" on "mp_profiles" ("slug");
CREATE INDEX "mp_questions_mp_profile_id_status_index" on "mp_questions" ("mp_profile_id", "status");
CREATE INDEX "site_settings_group_key_index" on "site_settings" ("group", "key");
CREATE UNIQUE INDEX "site_settings_key_unique" on "site_settings" ("key");
CREATE UNIQUE INDEX "pages_slug_unique" on "pages" ("slug");
CREATE UNIQUE INDEX "menus_location_unique" on "menus" ("location");
CREATE INDEX "menu_items_menu_id_parent_id_sort_order_index" on "menu_items" ("menu_id", "parent_id", "sort_order");
CREATE INDEX "news_type_is_active_created_at_index" on "news" ("type", "is_active", "created_at");
CREATE UNIQUE INDEX "news_slug_unique" on "news" ("slug");
CREATE UNIQUE INDEX "user_category_permissions_user_id_category_id_unique" on "user_category_permissions" ("user_id", "category_id");
COMMIT;
